mapper.select:
 
 select
 
 from perms.perms_t_notice where 1 = 1
 
              and cl_valid_starttime  >=   #{clValidStarttime}
 

              and cl_valid_endtime  <=   #{clValidEndtime}
 
 
              and cl_publish_status =  #{clPublishStatus}
 
 
              and (Replace(cl_summary,' ','') ilike  #{clSummary} or Replace(cl_title,' ','') ilike  #{clSummary})
 
 order by cl_valid_starttime

 
 select
 
 from perms.perms_t_notice t where 1 = 1 and cl_publish_status = 1 and cl_hotel_id = #{hotelId}
 
              and cl_valid_starttime  <=   #{nowDate}
 

              and cl_valid_endtime  >=   #{nowDate}
 
 
              and cl_publish_status =  #{status}
 
 and not EXISTS(select 1 from perms.perms_t_notice_account tt where tt.cl_notice_id = t.cl_id and cl_status = 1)
 order by cl_valid_starttime
