mapper.sql:
   		WITH RECURSIVE cre as (
			SELECT t1.* FROM perms.perms_t_department t1 WHERE t1.cl_id = #{cl_id} and t1.cl_status != ${@com.sstc.hmis.model.constants.Constants@STATUS_DEL}
			UNION ALL
			SELECT t2.* FROM perms.perms_t_department t2 INNER JOIN cre t3 on t3.cl_id = t2.cl_pid WHERE t2.cl_status != ${@com.sstc.hmis.model.constants.Constants@STATUS_DEL}
			)SELECT cl_id from cre

