 select count(0) from perms.perms_t_staff where cl_status != ${@com.sstc.hmis.model.constants.Constants@STATUS_DEL} 
 and cl_id in (
		SELECT distinct cl_staff_id FROM perms.perms_t_staff_hotel WHERE 
		cl_dpt_id in (
			
		)
		AND cl_status != ${@com.sstc.hmis.model.constants.Constants@STATUS_DEL} 
 )

 WITH RECURSIVE cre as (
	SELECT t1.cl_id,t1.cl_name,t1.cl_english_name,t1.cl_code FROM perms.perms_t_department t1 
	
		
			t1.cl_hotel_id = #{department.clHotelId}
		
		
			and t1.cl_status = #{department.clStatus}
		
		
			
				AND t1.cl_pid = #{department.clId}
			
			
			    AND t1.cl_pid ISNULL 
			
		
	
	
		UNION all 
		SELECT t3.cl_id,t3.cl_name,t3.cl_english_name,t3.cl_code from perms.perms_t_department t3 INNER JOIN cre t4 on t4.cl_id = t3.cl_pid
		
			
				t3.cl_status = #{department.clStatus}
			
		

	)
	SELECT t5.*,t6.cl_name as cl_org_name FROM cre t5 LEFT JOIN perms.v_dpt_org t6 on t5.cl_id = t6.cl_id
	
	where   (
			t5.cl_name ILIKE CONCAT('%',#{department.clName},'%') 
			OR t5.cl_english_name ILIKE CONCAT('%',#{department.clName},'%') 
		)

mapper.update:
update perms.perms_t_department set cl_status = ${@com.sstc.hmis.model.constants.Constants@STATUS_DEL},cl_modify_by = #{clModifyBy},cl_modify_time = #{clModifyTime}
WHERE cl_id IN 	
(
select cl_id from  () t1
)

update perms.perms_t_post_responsibility set cl_status = ${@com.sstc.hmis.model.constants.Constants@STATUS_DEL},cl_modify_by = #{clModifyBy}
WHERE cl_id in (select cl_post_id from perms.perms_t_dpt_post where cl_dpt_id IN 	
( select cl_id from  () t1 ))

update perms.perms_t_dpt_post set cl_status = ${@com.sstc.hmis.model.constants.Constants@STATUS_DEL},cl_modify_by = #{clModifyBy}
WHERE cl_dpt_id IN 	
(
select cl_id from  () t1
)

