mapper.select:
SELECT cl_id,cl_name,cl_type,'0' AS cl_pid FROM perms.perms_t_group_hotel 


		cl_id = #{clId} 	



			and cl_status = #{clStatus}


			and cl_status != ${@com.sstc.hmis.model.constants.Constants@STATUS_DEL}

SELECT p.cl_id,p.cl_english_name as cl_name,d.cl_dpt_id AS cl_pid FROM perms.perms_t_dpt_post d
LEFT JOIN perms.perms_t_post_responsibility p ON d.cl_post_id=p.cl_id
WHERE d.cl_dpt_id=#{cl_id}  and d.cl_status = ${@com.sstc.hmis.model.constants.Constants@STATUS_NORMAL}
 and p.cl_status = ${@com.sstc.hmis.model.constants.Constants@STATUS_NORMAL}

 	 SELECT  FROM perms.perms_t_group_hotel 


		 	 cl_id=#{hotel.clId} 


		 	 cl_pid=#{hotel.clPid} 


	 	 	AND cl_country_id =#{hotel.clCountryId}


	 	 	AND cl_province_id =#{hotel.clProvinceId}


	 	 	AND cl_city_id =#{hotel.clCityId}


	 	 	AND cl_country_code =#{hotel.clCountryCode}


	 	 	AND cl_province_code =#{hotel.clProvinceCode}


	 	 	AND cl_city_code =#{hotel.clCityCode}


	 	 	AND cl_level =#{hotel.clLevel}


		 	 AND cl_status != ${@com.sstc.hmis.model.constants.Constants@STATUS_DEL}


	 	 	AND cl_status =#{hotel.clStatus}


	 	  AND ( cl_code ILIKE CONCAT('%',#{hotel.clCode},'%')  OR cl_name ILIKE CONCAT('%',#{hotel.clCode},'%') OR cl_contacter ILIKE CONCAT('%',#{hotel.clCode},'%') )
