mapper.select:
SELECT * FROM perms.perms_t_post_responsibility WHERE CL_STATUS = 0 AND cl_id IN ( SELECT cl_post_id FROM perms.perms_t_dpt_post WHERE cl_status = 0
AND cl_grp_id = #{clGrpId}
AND cl_hotel_id = #{clHotelId}
And CL_DPT_ID IN ( WITH RECURSIVE cre AS ( SELECT t3.cl_id FROM perms.perms_t_department t3 WHERE t3.cl_id = #{clDptId} UNION ALL SELECT t4.cl_id FROM perms.perms_t_department t4 INNER JOIN cre t3 ON t3.cl_id = t4.cl_pid ) SELECT * FROM cre )
)
AND (cl_name ILIKE '%${clCondition}%' or cl_english_name ILIKE '%${clCondition}%')
order by CL_CREATE_TIME desc

SELECT * FROM perms.perms_t_post_responsibility WHERE CL_STATUS = 0 AND cl_id IN ( SELECT cl_post_id FROM perms.perms_t_dpt_post WHERE cl_status = 0
AND cl_grp_id = #{clGrpId}
AND cl_hotel_id = #{clHotelId}
And CL_DPT_ID = #{clDptId}
)
AND (cl_name ILIKE '%${clCondition}%' or cl_english_name ILIKE '%${clCondition}%')
order by CL_CREATE_TIME desc

  SELECT
		count(*)
	FROM
		perms.perms_t_dpt_post t1,
		perms.perms_t_post_responsibility t2
	WHERE
		t1.cl_post_id = t2.cl_id
	AND t2.cl_status = 0
	AND t1.cl_status = 0
	AND t2.cl_post_id = #{clPostId}
	AND t1.cl_dpt_id = #{clDptId}
	AND t2.cl_english_name = #{clEnglishName} 
	AND t2.cl_id != #{clId}
