mapper.select:
SELECT * FROM perms.PERMS_T_ROLE where cl_blockup = 0 and cl_status <> 1
And cl_belong_id = #{clBelongId}
and cl_status = #{clStatus}
and (cl_name ilike '%${clCondition}%' or cl_english_name ilike '%${clCondition}%')
order by cl_create_time desc

SELECT
DISTINCT
	t2.*
FROM
	perms.PERMS_T_STAFF_ROLE t1,
	perms.PERMS_T_ROLE t2
WHERE
	t1.CL_ROLE_ID = t2.cl_id
	and t1.cl_status = ${@com.sstc.hmis.model.constants.Constants@STATUS_NORMAL}
	and t2.cl_status = ${@com.sstc.hmis.model.constants.Constants@STATUS_NORMAL}
	AND t1.CL_STAFF_ID = #{clStaffId}

		and t2.cl_hotel_id in

	        	#{id}

SELECT DISTINCT * FROM perms.PERMS_T_ROLE t1 WHERE cl_id NOT IN ( SELECT t2.cl_id FROM perms.PERMS_T_STAFF_ROLE t1, perms.PERMS_T_ROLE t2 WHERE t1.CL_ROLE_ID = t2.cl_id AND t1.cl_status = 0 AND t2.cl_status = 0 AND t1.CL_STAFF_ID = #{clStaffId}
and t2.cl_hotel_id in
#{id}
)
and t1.cl_hotel_id in
#{id}
and t1.cl_status = 0

SELECT * FROM perms.perms_t_role t1 WHERE t1.cl_blockup = 0 AND t1.cl_status IN (0, 2) AND t1.cl_belong_id != #{clBelongId2}
AND t1.cl_belong_id = #{clBelongId}
AND cl_name ILIKE '%${clName}%'
AND t1.cl_name NOT IN ( SELECT cl_name FROM perms.perms_t_role WHERE cl_blockup = 0 AND cl_status IN (0, 2) AND cl_belong_id = #{clBelongId2} )
